package main

import "testing"

func Test_longestPalindrome(t *testing.T) {

	tests := []struct {
		name string
		s    string
		want int
	}{
		{
			"test1",
			"abccccdd",
			7,
		},
		{
			"test2",
			"a",
			1,
		},
		{
			"test3",
			"Aaaabbc",
			5,
		},
		{
			"test4",
			"",
			983,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := longestPalindrome(tt.s); got != tt.want {
				t.Errorf("longestPalindrome() = %v, want %v", got, tt.want)
			}
		})
	}
}
